```json ~/.config/zed/keymap.json
[
  {
    "context": "Workspace",
    "bindings": {
      "cmd-4": "workspace::ToggleBottomDock",
      "space u z": "workspace::ToggleCenteredLayout",
      "space u s": "zed::OpenSettings",
      "space u k": "zed::OpenKeymap"
    }
  },
  {
    "context": "Dock || Terminal || Editor",
    "bindings": {
      "ctrl-h": "workspace::ActivatePaneLeft",
      "ctrl-l": "workspace::ActivatePaneRight",
      "ctrl-k": "workspace::ActivatePaneUp",
      "ctrl-j": "workspace::ActivatePaneDown",

      // Switch between buffers
      "ctrl-shift-h": "pane::ActivatePrevItem",
      "ctrl-shift-l": "pane::ActivateNextItem"
    }
  },
  {
    "context": "Terminal",
    "bindings": {
      "cmd-k": "terminal::ScrollLineUp",
      "cmd-j": "terminal::ScrollLineDown"
    }
  },
  // File panel (netrw)
  {
    "context": "ProjectPanel && not_editing",
    "bindings": {
      "space space": "file_finder::Toggle",
      "a": "project_panel::NewFile",
      "A": "project_panel::NewDirectory",
      "r": "project_panel::Rename",
      "d": "project_panel::Delete",
      "x": "project_panel::Cut",
      "y": "project_panel::Copy",
      "p": "project_panel::Paste",

      // Navigate between panel
      "ctrl-h": "workspace::ActivatePaneLeft",
      "ctrl-l": "workspace::ActivatePaneRight",
      "ctrl-k": "workspace::ActivatePaneUp",
      "ctrl-j": "workspace::ActivatePaneDown"
    }
  },
  {
    "context": "vim_mode == normal || vim_mode == visual",
    "bindings": {
      "s": ["vim::PushSneak", {}],
      "S": ["vim::PushSneakBackward", {}]
    }
  },
  {
    "context": "EmptyPane || SharedScreen",
    "bindings": {
      "space space": "file_finder::Toggle"
    }
  },
  {
    "context": "Editor && vim_mode == normal && !VimWaiting && !menu",
    "bindings": {
      // put key-bindings here if you want them to work only in normal mode
      // Window movement bindings
      // Ctrl jklk to move between panes
      "ctrl-h": "workspace::ActivatePaneLeft",
      "ctrl-l": "workspace::ActivatePaneRight",
      "ctrl-k": "workspace::ActivatePaneUp",
      "ctrl-j": "workspace::ActivatePaneDown",

      "space o": "tab_switcher::Toggle",

      "space t": "workspace::NewCenterTerminal",

      // +LSP
      "space .": "editor::ToggleCodeActions",
      "space c a": "editor::ToggleCodeActions",
      "space c r": "editor::Rename",

      "cmd-1": "editor::ToggleFold",
      "space f a": "editor::FoldAll",
      "space f u": "editor::UnfoldAll",

      "g d": "editor::GoToDefinition",
      "g D": "editor::GoToDefinitionSplit",
      "g i": "editor::GoToImplementation",
      "g I": "editor::GoToImplementationSplit",
      "g t": "editor::GoToTypeDefinition",
      "g T": "editor::GoToTypeDefinitionSplit",
      "g r": "editor::FindAllReferences",

      // Go to next/prev error
      "g e": "editor::GoToDiagnostic",
      "g E": "editor::GoToPrevDiagnostic",

      // Symbol search
      "space s": "outline_panel::ToggleFocus",

      // Diagnostic
      "space x": "diagnostics::Deploy",

      // Git prev/next hunk
      "g n": "editor::GoToHunk",
      "g N": "editor::GoToPrevHunk",

      "space d": "editor::ToggleSelectedDiffHunks",

      // + Buffers
      // Switch between buffers
      "shift-h": "pane::ActivatePrevItem",
      "shift-l": "pane::ActivateNextItem",

      // Close active panel
      "space b d": "pane::CloseActiveItem",

      // Close other items
      "space b o": "pane::CloseInactiveItems",

      // File finder
      "space space": "file_finder::Toggle",

      // Project search
      "space /": "pane::DeploySearch",

      // Show project panel with current file
      "space e": "pane::RevealInProjectPanel"
    }
  }
]
